import React, { Component } from 'react'
import {
	View,
	Text,
	TextInput,
	StyleSheet,
	Dimensions,
	ActivityIndicator,
	ScrollView,
	Keyboard,
	TouchableNativeFeedback,
	TouchableWithoutFeedback,
} from 'react-native'

import colors from '../assets/colors'
import { connect } from 'react-redux'

import Svg, { Image, Circle, ClipPath } from 'react-native-svg'
import Animated, { Easing } from 'react-native-reanimated'
import { TapGestureHandler, State } from 'react-native-gesture-handler'

import * as firebase from 'firebase/app'
import 'firebase/auth'
import 'firebase/database'

const elevateValue = 50
const { width, height } = Dimensions.get('window')
const {
	Value,
	event,
	block,
	cond,
	eq,
	set,
	Clock,
	startClock,
	stopClock,
	debug,
	timing,
	clockRunning,
	interpolate,
	Extrapolate,
	concat,
} = Animated

const runTiming = (clock, value, dest) => {
	const state = {
		finished: new Value(0),
		position: new Value(0),
		time: new Value(0),
		frameTime: new Value(0),
	}

	const config = {
		duration: 1000,
		toValue: new Value(0),
		easing: Easing.inOut(Easing.ease),
	}

	return block([
		cond(clockRunning(clock), 0, [
			set(state.finished, 0),
			set(state.time, 0),
			set(state.position, value),
			set(state.frameTime, 0),
			set(config.toValue, dest),
			startClock(clock),
		]),
		timing(clock, state, config),
		cond(state.finished, stopClock(clock)),
		state.position,
	])
}

class LoginScreen extends Component {
	constructor() {
		super()
		this.state = {
			isLoading: false,
		}

		this.buttonOpacity = new Value(1)

		this.onStateChange = event([
			{
				nativeEvent: ({ state }) =>
					block([
						cond(
							eq(state, State.END),
							set(this.buttonOpacity, runTiming(new Clock(), 1, 0))
						),
					]),
			},
		])

		this.onCloseState = event([
			{
				nativeEvent: ({ state }) =>
					block([
						cond(
							eq(state, State.END),
							set(this.buttonOpacity, runTiming(new Clock(), 0, 1))
						),
					]),
			},
		])

		this.buttonY = interpolate(this.buttonOpacity, {
			inputRange: [0, 1],
			outputRange: [100, 0],
			extrapolate: Extrapolate.CLAMP,
		})

		this.bgY = interpolate(this.buttonOpacity, {
			inputRange: [0, 1],
			outputRange: [-height / 3 - elevateValue, 0],
			extrapolate: Extrapolate.CLAMP,
		})

		// text input
		this.textInputZindex = interpolate(this.buttonOpacity, {
			inputRange: [0, 1],
			outputRange: [1, -1],
			extrapolate: Extrapolate.CLAMP,
		})

		this.textInputY = interpolate(this.buttonOpacity, {
			inputRange: [0, 1],
			outputRange: [0, 100],
			extrapolate: Extrapolate.CLAMP,
		})

		this.textInputOpacity = interpolate(this.buttonOpacity, {
			inputRange: [0, 1],
			outputRange: [1, 0],
			extrapolate: Extrapolate.CLAMP,
		})

		// close button
		this.rotateCross = interpolate(this.buttonOpacity, {
			inputRange: [0, 1],
			outputRange: [180, 360],
			extrapolate: Extrapolate.CLAMP,
		})
  }

  onSignIn = async () => {
    if (this.state.email && this.state.password) {
      this.setState({ isLoading: true });
      try {
        const response = await firebase
          .auth()
          .signInWithEmailAndPassword(this.state.email, this.state.password);
        if (response) {
          this.setState({ isLoading: false });
          this.props.signIn(response.user);
          //this.props.navigation.navigate("HomeScreen");
        }
      } catch (error) {
        this.setState({ isLoading: false });
        switch (error.code) {
          case "auth/user-not-found":
            alert("A user with that email does not exist. Try signing Up");
            break;
          case "auth/invalid-email":
            alert("Please enter an email address");
        }
      }
    }
  };

	renderAuthContainer = () => {
		return (
			<TouchableWithoutFeedback onPress={() => Keyboard.dismiss()}>
				<Animated.View
					style={{
						zIndex: this.textInputZindex,
						opacity: this.textInputOpacity,
						height: height / 3,
						...StyleSheet.absoluteFill,
						top: null,
						justifyContent: 'center',
						transform: [{ translateY: this.textInputY }],
					}}
				>
					<TapGestureHandler onHandlerStateChange={this.onCloseState}>
						<Animated.View
							onTouchStart={(Keyboard.dismiss, this.handleLoginOnClick())}
							style={styles.closeButton}
						>
							<Animated.Text
								style={{
									fontSize: 15,
									transform: [
										{
											rotate: concat(this.rotateCross, 'deg'),
										},
									],
								}}
							>
								X
							</Animated.Text>
						</Animated.View>
					</TapGestureHandler>

					<Text style={{ marginStart: 30 }}>Phone number</Text>
					<TextInput
						placeholder='Ex: (012) 345-6789'
						style={styles.textInput}
					/>
					{/* send code text btn */}
					<TouchableNativeFeedback>
						<View style={styles.sendCodeText}>
							<Text style={{ fontSize: 30, fontWeight: 'bold' }}>Continue</Text>
						</View>
					</TouchableNativeFeedback>
				</Animated.View>
			</TouchableWithoutFeedback>
		)
	}

	render() {
		return (
			<View style={{ flex: 1, backgroundColor: 'white' }}>
				{this.state.isLoading ? (
					<View
						style={[
							StyleSheet.absoluteFill,
							{
								alignItems: 'center',
								justifyContent: 'center',
								zIndex: 1000,
								elevation: 1000,
							},
						]}
					>
						<ActivityIndicator size='large' color={colors.logoColor} />
					</View>
				) : null}
				<View style={{ flex: 1 }}>
					<ScrollView style={{ flex: 1 }}>
						<View style={styles.container}>
							{/* background image */}
							<Animated.View
								style={{
									...StyleSheet.absoluteFill,
									transform: [{ translateY: this.bgY }],
								}}
							>
								<Svg height={height + elevateValue} width={width}>
									<ClipPath id='clip'>
										<Circle r={height + elevateValue} cx={width / 2} />
									</ClipPath>
									<Image
										href={require('../assets/background_login.jpg')}
										width={width}
										height={height + elevateValue}
										preserveAspectRatio='xMidYMid slice'
										clipPath='url(#clip)'
									/>
								</Svg>
							</Animated.View>

							{/* button container */}
							<View style={styles.buttonContainer}>
								{/* login button */}
								<TapGestureHandler onHandlerStateChange={this.onStateChange}>
									<Animated.View
										style={{
											...styles.button,
											opacity: this.buttonOpacity,
											transform: [{ translateY: this.buttonY }],
										}}
									>
										<Text style={{ fontSize: 20, fontWeight: 'bold' }}>
											Sign in
										</Text>
									</Animated.View>
								</TapGestureHandler>

								{/* register button */}
								<TapGestureHandler onHandlerStateChange={this.onStateChange}>
									<Animated.View
										style={{
											...styles.button,
											opacity: this.buttonOpacity,
											transform: [{ translateY: this.buttonY }],
										}}
									>
										<Text style={{ fontSize: 20, fontWeight: 'bold' }}>
											Register
										</Text>
									</Animated.View>
								</TapGestureHandler>
							</View>
							{this.renderAuthContainer()}
						</View>
					</ScrollView>
				</View>
			</View>
		)
	}
}

const mapDispatchToProps = dispatch => {
	return {
		signIn: user => dispatch({ type: 'SIGN_IN', payload: user }),
		signOut: () => dispatch({ type: 'SIGN_OUT' }),
	}
}

export default connect(null, mapDispatchToProps)(LoginScreen)

const styles = StyleSheet.create({
	container: {
		flex: 1,
		backgroundColor: colors.bgMain,
	},
	textInput: {
		height: 50,
		borderWidth: 0.5,
		borderColor: colors.borderColor,
		marginHorizontal: 40,
		marginBottom: 10,
		color: 'black',
		paddingHorizontal: 10,
	},
	loginButtons: {
		borderWidth: 0.5,
		backgroundColor: 'transparent',
		marginBottom: 10,
		width: 200,
	},
})
